  <script>
    $('.payment-item').on('click', function () {
      $('#payment_id').val($(this).data('payment_id'));

      if ($(this).hasClass('steam')) {
        $('.styles__FlexContainer-sc-n3x62l-17').hide();
        $('.styles__Money-sc-n3x62l-11').hide();
        $('#btn-buy').hide();
        $('#btn-buy-tebex').hide();
        $('#btn-buy-steam').show();
        $('.tebex-text').hide();
        $('.steam-text').show();
        $('.unipay-fields').hide();
        $('.alfa-text').hide();
        $('.unipay-fields input').attr('required', false);
        $('#btn-buy').text('{{ __('Пополнить баланс') }}');
      } else if ($(this).hasClass('unitpay')) {
        $('.styles__FlexContainer-sc-n3x62l-17').show();
        $('.styles__Money-sc-n3x62l-11').show();
        $('#btn-buy').show();
        $('#btn-buy-steam').hide();
        $('.steam-text').hide();
        $('.tebex-text').hide();
        $('.alfa-text').hide();
        $('.unipay-fields').show();
        $('.unipay-fields input').attr('required', true);
        $('#btn-buy').text('{{ __('Пополнить баланс') }}');
      } else if ($(this).hasClass('tebex_OFF')) {
        $('.styles__FlexContainer-sc-n3x62l-17').hide();
        $('.styles__Money-sc-n3x62l-11').hide();
        $('#btn-buy').show();
        $('#btn-buy-steam').hide();
        $('.tebex-text').show();
        $('.steam-text').hide();
        $('.alfa-text').hide();
        $('.unipay-fields').hide();
        $('.unipay-fields input').attr('required', false);
        $('#btn-buy').text('{{ __('Открыть магазин') }}');
      } else if ($(this).hasClass('tebex')) {
        $('.styles__FlexContainer-sc-n3x62l-17').show();
        $('.styles__Money-sc-n3x62l-11').show();
        $('#btn-buy').show();
        $('#btn-buy').text('{{ __('Пополнить баланс') }}');
        $('#amount').val('100');
        $('#amount-total').val('100');
        $('.alfa-text').hide();
      } else if ($(this).hasClass('alfa_OFF')) {
        $('.styles__FlexContainer-sc-n3x62l-17').hide();
        $('.styles__Money-sc-n3x62l-11').hide();
        $('#btn-buy').hide();
        $('#btn-buy-steam').hide();
        $('.tebex-text').hide();
        $('.steam-text').hide();
        $('.alfa-text').show();
        $('.unipay-fields').hide();
      } else {
        $('.styles__FlexContainer-sc-n3x62l-17').show();
        $('.styles__Money-sc-n3x62l-11').show();
        $('#btn-buy').show();
        $('#btn-buy-steam').hide();
        $('.steam-text').hide();
        $('.tebex-text').hide();
        $('.alfa-text').hide();
        $('.unipay-fields').hide();
        $('.unipay-fields input').attr('required', false);
        $('#btn-buy').text('{{ __('Пополнить баланс') }}');
      }

    });

    $('#email').on('click', function () {
      $('.unipay-fields').removeClass('check-error');
    });

    $('#btn-buy').on('click', function () {
	  if ($('#payment_id').val() === "") {
        return;
      }

      $('input[name="term"]').removeClass('check-error');
      $('input[name="policy"]').removeClass('check-error');

      if(!$('input[name="term"]').is(':checked'))
      {
        $('input[name="term"]').addClass('check-error');
        return false;
      }
      if(!$('input[name="policy"]').is(':checked'))
      {
        $('input[name="policy"]').addClass('check-error');
        return false;
      }
      if(($('#payment_id').val() == '45' || $('#payment_id').val() == '451') && $('#email').val() == '')
      {
        $('.unipay-fields').addClass('check-error');
        return false;
      }
        $('#topup-balance-form').submit();
    });

    $('#apply-promocode').on('click', function () {
      $('#code').val($('#promocode').val());
      $('#promocode-form').submit();
    });

    $('#amount').on('change', function () {
      let amount = 0;
      @if(Session::get('deposit_bonus', 0) > 0)
        amount = parseFloat({{ Session::get('deposit_bonus', 1) }} * $(this).val() / 100) + parseFloat($(this).val());
      @else
        amount = $(this).val();
      @endif
      $('#amount-total').val(amount);
    });

    $('#topup').on('click', function () {
      $('.balance-modal').show();
    });
    $('.select-stats .option').on('click', function () {
      console.log($(this).data('type'));
      location.href = "{{ route('stats') }}?type=" + $(this).data('type') + "&search=" + $('#search').val();
